import React, { useState } from 'react';
import { Container, Form, ListGroup, Button } from 'react-bootstrap';

function MusicPlayer() {
  const [search, setSearch] = useState('');
  const [results, setResults] = useState([]);
  const [currentPreview, setCurrentPreview] = useState(null);
  const [error, setError] = useState('');

  const searchSongs = async () => {
    if (!search.trim()) return;
    const url = `https://api.deezer.com/search?q=${encodeURIComponent(search)}`;

    try {
      /* ‚îÄ‚îÄ Option A: fetch directly (works in most browsers) ‚îÄ‚îÄ */
      const res = await fetch(`https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`);

      /* ‚îÄ‚îÄ Option B: if CORS isn‚Äôt an issue in your region: ‚îÄ‚îÄ */
      // const res = await fetch(url);

      if (!res.ok) throw new Error(`${res.status}‚ÄØ${res.statusText}`);
      const data = await res.json();
      setResults(data.data || []);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Could not reach Deezer API.');
    }
  };

  return (
    <Container className="mt-5">
      <h2>üé∂ Deezer Music Preview</h2>

      <Form.Control
        type="text"
        placeholder="Search for a song‚Ä¶"
        value={search}
        onChange={e => setSearch(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && searchSongs()}
        className="my-3"
      />

      <Button onClick={searchSongs} variant="primary" className="mb-3">
        Search
      </Button>

      {error && <p className="text-danger">{error}</p>}

      <ListGroup>
        {results.map(track => (
          <ListGroup.Item
            key={track.id}
            className="d-flex justify-content-between align-items-center"
          >
            {track.title}¬†‚Äì¬†{track.artist.name}
            <Button
              variant="outline-success"
              size="sm"
              onClick={() => setCurrentPreview(track.preview)}
              disabled={!track.preview}
            >
              ‚ñ∂Ô∏è Play
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {currentPreview && (
        <div className="mt-4">
          <audio controls autoPlay src={currentPreview} style={{ width: '100%' }} />
        </div>
      )}
    </Container>
  );
}

export default MusicPlayer;
